INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22222, 'Hello, %s.  How may I help you?', NULL, 0, 0, 0, 3, 1, 0, 0, 1, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22005, 'Ahoy there, stout adventurer! Dragon artifacts you say? Nay! But I\'ve got a nugget of information for ya. There\'s a friendly feller hangin\' out near the Temple of Atal\'Hakkar. Don\'t be fretting when you spot him, he\'s as friendly as a frost ram. Word is he\'s keen on dragon artifacts, especially those from the black dragon swarm. Give him a nod and see what tales he\'s got to share. Safe travels!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22004, 'No, I cannot take these! But I know of a knowledgeable druid in the north of Winterglade, studying the moonkin. In the past, he sought artifacts, particularly those linked to the black dragon swarm. I can\'t say for sure if they still harbor the same interest, but it might be worth seeking him out.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22003, 'Hello there, brave adventurer! No, I am not interested in these, but I\'ve heard tell of a keen-eyed High Elf in the Blasted Lands, closely observing the local dragons. It\'s likely they have an interest in artifacts, perhaps even those from the black dragon swarm. If you\'re headed in that direction, seek him out and share your findings.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22002, 'No, I am not interested in these. But I heared there\'s a perceptive High Elf in the Blasted Lands, studying the dragons that roam the desolate lands. Rumor has it, they might be intrigued by artifacts, especially those from the black dragon swarm. If you find yourself in the Blasted Lands, seek them out. He might have something of value to offer in return for those intriguing artifacts. ', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22001, 'No, Mon! But I got wind of sometin\' interestin\' for ya. There\'s a friendly soul near the Temple of Atal\'Hakkar. Don\'t be alarmed when ya see him, he\'s a good one. Might have some useful knowledge or maybe he might have something of value to offer in return. Check it out, and may your journey be filled with good vibes!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22000, 'No, I am sorry, I am not interested in these. But an old friend of mine, a druid in Winterglade, who delves into the mysteries of the moonkin. He is studying them as far as I know. If you happen to be journeying in that direction, look for the wise one. There\'s a chance they\'re still fascinated by artifacts, especially those tied to the black dragon swarm. Who knows, he might have something of value to offer in return for those intriguing artifacts. ', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
