INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22008, 'Hello, traveler. I\'m Nawat Ravenrider. I am soaking up the wonders of moonkin life here in Winterspring. I guess you have heard, I\'ve got a soft spot for dragon artifacts. If you spot any during your journey, swing by and maybe we can work out a little deal. Enjoy the woods, and feel free to drop by anytime.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22009, 'Salutations, traveler! I am Tyranikus, a humble emissary of Ysera herself, sent to these swampy lands to collect artifacts from the black dragon swarm. Fear not, for I am no threat to you. In fact, I come in peace and seek to trade. If you happen upon any dragon artifacts in your adventures, I\'d be delighted to exchange them for something of value. Your cooperation is much appreciated, and may your travels through the Swamp of Sorrows be safe and prosperous.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
