INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22013, 'Ho there, wanderer! Behold this Hearthstone – a rare gem in these parts. What\'s it do, you ask? Well, it\'s a swift ticket back to Thunder Bluff, no matter where you roam. No more fuss with different hearthstones – this one\'s a straight shot. Crafted with the grace of tauren wisdom and arcane flair, it\'s a rare find. We\'ve got just a few in stock, so if you\'re yearning for the pinnacle of travel ease, consider it yours.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22014, 'Hail, warrior! This is a rare treasure in Orgrimmar. Wondering what it does? Simple – it zips you straight back here, no matter where you\'re adventuring. No more juggling different hearthstones – this one\'s your direct route. Forged with orcish strength and arcane prowess, it\'s a prized possession. ', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22015, 'This is a rare gem found in the eerie corners of Undercity. Wondering what sets it apart? Quite simple – it whisks you straight back to Undercity, no matter where you tread. No more fumbling with various hearthstones – this one\'s your direct path. Forged with undead resilience and arcane mastery, it\'s a coveted find. Limited in stock, so if you desire the epitome of travel ease, this is your chance.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22016, 'This is a rare treasure in the heart of Stormwind. Curious about its powers? Simple – it swiftly transports you back to Stormwind, no matter where your quests take you. This one\'s your shortcut. Crafted with human precision and arcane mastery, it\'s a distinguished possession. Limited supply, so if you seek the pinnacle of travel ease, this is your solution.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22017, 'A rare find in these parts. Jump straight to Ironforge, bypassing the usual hearthstone fuss. Crafted with dwarven precision and arcane flair, these gems are as exclusive as they come. Limited stock, so don\'t dawdle.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `sound_id`, `language_id`, `emote_id1`, `emote_id2`, `emote_id3`, `emote_delay1`, `emote_delay2`, `emote_delay3`) VALUES (22018, 'A rare treasure nestled in Darnassus. Wondering what sets it apart? Quite simply, it swiftly spirits you back to here, no matter where your nocturnal ventures lead. Bid farewell to the complexities of other hearthstones – this one\'s your celestial shortcut. Crafted with night elf finesse and arcane mastery, it\'s a unique possession. Limited availability, so be quick.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
